sp_widget.client_script:
function($rootScope, $scope) {
	/* widget controller */
	var c = this;

	c.selectedItem = function(idx) {
		var id = c.data.notes[idx].sys_id;
		console.log("Notes ID: " + id);
		$rootScope.noteID = id;
		$rootScope.$emit('selectNote', id);
	}

	$rootScope.$on('updatedNote', function(event, data) {
		console.log("Update Listener caught note ID " + $rootScope.noteID);

